/* "Cart" inline script from theme/fisherway/template/module/cart.tpl */
function update(key, elem) {
	quantity = $(elem).val();
	$('#cart').load('index.php?route=module/cart&quantity=' + quantity + '&product_id=' + key + ' #cart > *');
}
function select_option(key, elem) {
	var option_id = $(elem).attr("name");
	var option_value_id = $(elem).find("option:selected").val();
	$('#cart').load('index.php?route=module/cart&remove=' + key + ' #cart > *');
	data = new Object();
	data["product_id"] = key;
	data[option_id] = option_value_id;
	$.ajax({
		url: 'index.php?route=checkout/cart/add', type: 'post', data: data, dataType: 'json',
		success: function(json) {
			if (json['success']) {
				$('#cart').load('index.php?route=module/cart #cart > *');
				$('#cart-total').html(json['total']);
			}
		}
	});
}
function coupon() {
	coupon_cod = $("input[name='coupon']").val();
	$('#cart').load('index.php?route=module/cart&coupon=' + coupon_cod + ' #cart > *');
}


/* pdsListRolloever ("Product Series" inline script from header)*/
 
$(document).ready(function(){
	pdsListRolloever();
});
function pdsListRolloever(){
	$('.pds a.pds-thumb-rollover').hover(function(){
		//on hover
		$this = $(this);
		var hoverImage = $this.attr('rel');
		$this.parent().parent().find('.image a img').attr('src', hoverImage);
	}, function(){
		//on unhover
		$this = $(this);
		var masterImage = $this.attr('master-image');
		$this.parent().parent().find('.image a img').attr('src', masterImage);
	});
}

/* plpopups cached */
setTimeout(function() {
	$(function(){
		if (!$.cookie('fisherwa_cookie')){$('#boxUserFirstInfo').plpopup({closeOnOverlayClick: true, closeOnEsc: true});}
		$.cookie('fisherwa_cookie', true, {expires: 20, path: '/'});  
	})  
}, 3000);

