$(document).ready(function(){
	//show hidden variants at series
	$('#showHidden span').bind('click', function(){
		var unavailable = $('#hiddenUnavailable');
		var visible = unavailable.css('display') == 'none' ? false : true;
		if (visible){
			unavailable.slideUp(600);
			$(this).html('Показать все');
		} else {
			unavailable.slideDown(600);
			$(this).html('Показать в наличии');
		}
	});
	
	//fix body height
	var contH = parseInt($('#container').height());
	var diff = contH - parseInt($('#ocfilter-1').height()) - 170;
	if ($('#ocfilter-1').size() > 0 && diff < 0) {
		$('#container').css('height', contH - diff + 'px');
		$('#footer').css('bottom', diff + 'px');
		
	}
	$('.product-info').css('overflow', 'hidden');
	
	//fancybox
	$('.fancybox, a[rel="gallery"], a[rel="thumbnail"]').fancybox({
		wrapCSS    : 'fancybox-custom',
		openEffect : 'elastic',
		openSpeed  : 150,
		closeEffect : 'elastic',
		closeSpeed  : 150,
		closeClick : true,
		helpers : {
			title : {type : 'inside'},
			overlay : {css : {'background' : 'rgba(168,168,168,0.85)'}},
			/*thumbs : {width  : 50,height : 50}*/
		},
		closeBtn  : false,
		nextClick : true
	});		
	
	$("#fancybox-pictures .scrollable").scrollable({
		'size':1,
		'circular':true,
		'onSeek': function(){
			var api = $("#fancybox-pictures .scrollable").data("scrollable");
			var n = api.getIndex();
			$('#fancybox-pictures .picture-pagination li').removeClass('current');
			$('#fancybox-pictures .picture-pagination li:eq('+n+')').addClass('current');
		}
	});
	
	$('.mLink').click( function() {
			if($('.topmenu').css('visibility') == 'visible') {
				$('.topmenu').css('visibility','hidden');
			}
			else {
				$('.topmenu').css('visibility','visible');
			}
	});

	$('.closeBtn').click( function() {
	  		$('.topmenu').css('visibility','hidden');
	});

	  $('#mainNav').click( function() {
		$('#supermenu').slideToggle(400, function() {
			if($('#mainNav').hasClass('active')) {
				$('#mainNav').removeClass('active');
				$('#container').css('margin-top','');
				$('#supermenu').css('display','none');
			} else {
				$('#supermenu').css('display','block');
				$('#container').css('margin-top','90px');
				$('#mainNav').addClass('active');
			}
		});
	  });	
});

$(window).resize(function(){
	var w = parseInt($(window).width());
	if (w > 851){
		$('#supermenu').css('display','');
		$('#mainNav').removeClass('active');
	}
});

window.scrollImgTo = function(n){
	var api = $("#fancybox-pictures .scrollable").data("scrollable");
	api.seekTo(n, 150);
	$('#fancybox-pictures .picture-pagination li').removeClass('current');
	$('#fancybox-pictures .picture-pagination li:eq('+n+')').addClass('current');
}
//Promotions
function pcolorbox () {
  $(".pcolorbox").colorbox({inline:true, width:"500px", opacity: 0.5});  
}

$(document).ready(function(){
  pcolorbox();
});
//EOF Promotions
//Custom Menu
$(document).ready(function() {

	href = location.pathname.substr(1);
	//$('.box-category a[href*="'+href+'"]').parent().addClass('active');

	//$('.box_category li div').parent().children('a').append('&nbsp; <i style="text-decoration:none;">&gt;</i>')

	var menu_width = $('.box_category').width();
	$('#column-left .box_category li div').css('left', menu_width);
	$('#column-right .box_category').addClass('right');
	$('#column-right .box_category li div').css('right', menu_width).addClass('right');
	$('#column-left .box_category li div ul').css('min-width', menu_width);
	$('#column-right .box_category li div ul').css('min-width', menu_width);




	$('.product-menu ul li a').click(function(e){
	    e.preventDefault();
	    $('html, body').animate({'scrollTop':$('#'+$(this).attr('href').split('#')[1]).offset().top}, 300)
	    
	})



});
//EOF Custom menu